import React from "react";

import { useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import Fab from "@mui/material/Fab";
import AddIcon from "@mui/icons-material/Add";

const List = (props) => {
  const my_lists = useSelector((state) => state.dictionary.list);

  const history = useHistory();
  //화면 출력
  return (
    <div style={{ display: "flex" }}>
      <div style={{ textAlign: "center", fontSize: "50px" }}>My Dictionary</div>
      <div style={{ display: "flex", flexWrap: "wrap", textAlign: "center" }}>
        {my_lists.map((list, index) => {
          return (
            <div
              key={index}
              style={{
                minWidth: "20vw",
                minHeight: "25vh",
                padding: "30px",
                margin: "8px",
                border: "1px solid #ddd",
                backgroundColor: "aliceblue",
              }}
            >
              <p style={{ fontSize: "3px", textDecoration: "underline" }}>
                단어
              </p>
              <div>{list.word}</div>
              <p style={{ fontSize: "3px", textDecoration: "underline" }}>
                설명
              </p>
              <div>{list.explanation}</div>
              <p style={{ fontSize: "3px", textDecoration: "underline" }}>
                예시
              </p>
              <div style={{ color: "blue" }}>{list.example}</div>
            </div>
          );
        })}
      </div>
      <Fab
        aria-label="add"
        style={{
          position: "fixed",
          right: "10px",
          bottom: "10px",
        }}
        onClick={() => {
          history.push("/add");
        }}
      >
        <AddIcon />
      </Fab>
    </div>
  );
};

export default List;
