import React from "react";

import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import {
  loadDictionaryFB,
  deleteDictionaryFB,
} from "./redux/modules/dictionary";
import Fab from "@mui/material/Fab";
import AddIcon from "@mui/icons-material/Add";
import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import InfoIcon from "@mui/icons-material/Info";

const List = (props) => {
  const my_lists = useSelector((state) => state.dictionary.list);
  const dispatch = useDispatch();

  const deleteBtn = (id) => {
    if (window.confirm("삭제 하시겠습니까?")) {
      dispatch(deleteDictionaryFB(id));
      dispatch(loadDictionaryFB());
    } else {
      return false;
    }
  };

  const history = useHistory();
  //화면 출력
  return (
    <div>
      <div style={{ textAlign: "center", fontSize: "50px" }}>My Dictionary</div>
      <div style={{ height: "100vh", width: "100vw", display: "flex" }}>
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "center",
          }}
        >
          {my_lists.map((list, index) => {
            return (
              <div
                key={index}
                style={{
                  minWidth: "10%",
                  minHeight: "30%",
                  padding: "30px",
                  margin: "8px",
                  border: "1px solid #ddd",
                  backgroundColor: "aliceblue",
                }}
              >
                <div style={{ display: "flex", justifyContent: "right" }}>
                  <DeleteForeverIcon
                    onClick={() => {
                      deleteBtn(list.id);
                    }}
                  ></DeleteForeverIcon>
                  <InfoIcon></InfoIcon>
                </div>
                <p style={{ fontSize: "3px", textDecoration: "underline" }}>
                  단어
                </p>
                <div>{list.word}</div>
                <p style={{ fontSize: "3px", textDecoration: "underline" }}>
                  설명
                </p>
                <div>{list.explanation}</div>
                <p style={{ fontSize: "3px", textDecoration: "underline" }}>
                  예시
                </p>
                <div style={{ color: "blue" }}>{list.example}</div>
              </div>
            );
          })}
        </div>
        <Fab
          aria-label="add"
          style={{
            position: "fixed",
            right: "10px",
            bottom: "10px",
          }}
          onClick={() => {
            history.push("/add");
          }}
        >
          <AddIcon />
        </Fab>
      </div>
    </div>
  );
};

export default List;
